import React from 'react';
import { Link } from 'react-router-dom';

function PokemonBox({pokemons}) {

    return pokemons.map((pokemon, index) => (
        <div className="section--list__inner" key={index}>
            <Link className="section--list__link" to={{
                pathname: `/pokemon/${index+1}`,
                state: {
                    'name': pokemon.name,
                    'imgUrl': pokemon.imgUrl
                }
            }}
            >
                <figure className="photo">
                    <img className="photo__img" src={pokemon.imgUrl} alt={pokemon.name} width='200'/>
                </figure>
            </Link>
        </div>
    ))
}

export default PokemonBox;
