import React from 'react';
import { BrowserRouter, Switch, Route } from 'react-router-dom';
import './App.css';
// import PokemonBox from "./Components/PokemonList/PokemonList";
// import LoadScreen from "./Components/LoadScreen/LoadScreen";
import PokemonDetails from "./Components/PokemonDetails/PokemonDetails";
import PokemonList from "./Components/PokemonList/PokemonList";


class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            items: []
        }
    }

    componentDidMount() {
        fetch('https://pokeapi.co/api/v2/pokemon?limit=9')
            .then(res => res.json())
            .then((result) => {
                const url =result.results.map(res => {
                    return res.url
                });

                Promise.all([...url].map((url) =>
                    fetch(url)
                        .then(res => res.json())
                ))
                    .then(res => {
                        this.setState({
                            isLoading: false,
                            items: [...res].map(res => {
                                return { 'name': res.species.name, 'imgUrl': res.sprites.front_default}
                            })
                        });
                    })
            })
    }

    render() {
        const pokemons = this.state.items;
        console.log(pokemons);

        return (
            <BrowserRouter>
                <Switch>
                    <Route pokemons={pokemons} path="/" component={PokemonList} exact/>
                    <Route path="/pokemon/:id" component={PokemonDetails}/>
                </Switch>
            </BrowserRouter>
        )
    }
}

export default App;


